import java.util.Map;
import java.util.HashMap;

/**
 * 567. 字符串的排列
 * https://leetcode-cn.com/problems/permutation-in-string/
 */
class CheckInclusin {
    public static void main(String[] args) {
        SolutionMySelf mySelf = new SolutionMySelf();
        if(mySelf.checkInclusion("", 
                                "")) {
            System.out.print("true");
        }else{
            System.out.print("false");
        }
    }
}

/**
 * 自己用map写的，不通过，不知道错在哪里了
 * 测试用例如下：
 * s1 = ""
 * s2 = ""
 */
class SolutionMySelf{
    public boolean checkInclusion(String s1, String s2) {

        if(s1.length() > s2.length()) {
            return false;
        }

        Map<Character,Integer> map1 = new HashMap<>();
        Map<Character,Integer> map2 = new HashMap<>();
        
        for (int i = 0; i < s1.length(); i++) {
            int value = (map1.get(s1.charAt(i)) == null ? 0 : map1.get(s1.charAt(i))) + 1;
            map1.put(s1.charAt(i), value);
        }
        
        int l = 0;
        for (int i = 0; i < s2.length(); i++) {

            int value = (map2.get(s2.charAt(i)) == null ? 0 : map2.get(s2.charAt(i)) )+ 1;
            map2.put(s2.charAt(i), value);
            if (i > s1.length() - 1) {
                value = map2.get(s2.charAt(l)) - 1;
                map2.put(s2.charAt(l), value);
                if(map2.get(s2.charAt(l)) == 0) {
                    map2.remove(s2.charAt(l));
                }
                l++;
            }
            if(mapMatchs(map1, map2)) {
                return true;
            }
        }
        return mapMatchs(map1, map2);
    }

    private boolean mapMatchs(Map<Character, Integer> map1, Map<Character, Integer> map2) {
       for (Map.Entry<Character, Integer> entry : map1.entrySet()) {
           if(map2.get(entry.getKey()) != entry.getValue()) {// 他们的value有一个不相等的，就说明s1全排列里面没有s2的子串
                return false;
           }
       }
       return true;
    }
}

/**
 * 滑动窗口的方法
 */
class SolutionGood {
    public boolean checkInclusion(String s1, String s2) {
        if (s1.length() > s2.length())
            return false;
        int[] s1map = new int[26];
        int[] s2map = new int[26];
        for (int i = 0; i < s1.length(); i++) {
            s1map[s1.charAt(i) - 'a']++;
            s2map[s2.charAt(i) - 'a']++;
        }
        for (int i = 0; i < s2.length() - s1.length(); i++) {
            if (matches(s1map, s2map))
                return true;
            s2map[s2.charAt(i + s1.length()) - 'a']++;
            s2map[s2.charAt(i) - 'a']--;
        }
        return matches(s1map, s2map);
    }
    public boolean matches(int[] s1map, int[] s2map) {
        for (int i = 0; i < 26; i++) {
            if (s1map[i] != s2map[i])
                return false;
        }
        return true;
    }
}


/**
 * 暴力解法，提交的时候超时了
 */
class Solution {
    boolean flag = false;

    public boolean checkInclusion(String s1, String s2) {
        permute(s1, s2, 0);
        return flag;
    }

    public String swap(String s, int i0, int i1) {
        if (i0 == i1)
            return s;
        String s1 = s.substring(0, i0);
        String s2 = s.substring(i0 + 1, i1);
        String s3 = s.substring(i1 + 1);
        return s1 + s.charAt(i1) + s2 + s.charAt(i0) + s3;
    }

    void permute(String s1, String s2, int l) {
        if (l == s1.length()) {
            if (s2.indexOf(s1) >= 0)
                flag = true;
        } else {
            for (int i = l; i < s1.length(); i++) {
                s1 = swap(s1, l, i);
                permute(s1, s2, l + 1);
                s1 = swap(s1, l, i);
            }
        }
    }
}
